import React from 'react'
import PlayCircleFilledIcon from "@material-ui/icons/PlayCircleFilled";
import PauseCircleOutlineIcon from "@material-ui/icons/PauseCircleOutline";
import FavoriteIcon from "@material-ui/icons/Favorite";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";

import { useStateProviderValue } from '../../Context/StateProvider'
import Header from '../Header'
import SongRow from '../SongRow'

import './style.css'

function Body({ spotify }) {

  const [{ discover_weekly, playing }, dispatch] = useStateProviderValue();
  const playPlaylist = (id) => {
    spotify
      .play({
        context_uri: `spotify:playlist:37i9dQZEVXcWeWFZt4d81O`,
      })
      .then((res) => {
        spotify.getMyCurrentPlayingTrack().then((r) => {
          dispatch({
            type: "SET_ITEM",
            item: r.item,
          });
          dispatch({
            type: "SET_PLAYING",
            playing: true,
          })
        })
      })
  }

  const playSong = (id) => {
    spotify
      .play({
        uris: [`spotify:track:${id}`],
      })
      .then((res) => {
        spotify.getMyCurrentPlayingTrack().then((r) => {
          dispatch({
            type: "SET_ITEM",
            item: r.item,
          })
          dispatch({
            type: "SET_PLAYING",
            playing: true,
          })
        })
      })
  }
  return (
    <div className="body">
      <Header spotify={spotify} />

      <div className="body__info">
        <img src={discover_weekly?.images[0].url} alt={discover_weekly?.name} />
        <div className="body__infoText">
          <strong>PLAYLIST</strong>
          <h2>{discover_weekly?.name}</h2>
          <p>{discover_weekly?.description}</p>
        </div>
      </div>

      <div className="body__songs">
        <div className="body__icons">
          {playing ? (
            <PauseCircleOutlineIcon className="body__shuffle" onClick={playPlaylist} />
          ) : (
              <PlayCircleFilledIcon className="body__shuffle" onClick={playPlaylist} />
            )}

          <FavoriteIcon fontSize="large" />
          <MoreHorizIcon />
        </div>
        {discover_weekly?.tracks.items.map((item, id) =>
          <div key={id}><SongRow track={item.track} playSong={playSong} /></div>
        )}
      </div>
    </div>
  )
}

export default Body
