export const initialState = {
  user: null,
  // change token to null after development //TODO
  //token: null,
  token: "BQC8HRtRl0Uh3xqAR0LhUOTlzRyA_9c0F5xebXh5EFuRRytkV28msNlhZzZFELfLr6edDGRmfYxwUQTv4Q-aKmP8lMGX0smtpcCiyZlB6cay9DvWtfkBRMCJ0ba1IrLXERyEmykb900_8vBWlipkRtPg0oB_",
  playlists: [],
  playing: false,
  item: null
}

const reducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        user: action.user
      }
    case 'SET_TOKEN':
      return {
        ...state,
        token: action.token
      }
    case 'SET_PLAYLISTS':
      return {
        ...state,
        playlists: action.playlists
      }
    default:
      return state

  }

}

export default reducer;
