export const initialState = {
  user: null,
  // change token to null after development //TODO
  token: null,
  //token: "BQDW280RSGaUI4v_R1py2ZBzurI1WXxKk5Zg2VxfVRx_zdgKErXMAvx9zPt7-tZ6wsXLF-2vPeyy-1gXc1ea_Wwd1vyHGa4oZGUL9-jJKUdA2uhnhnTJIi87kaTeoJZBHa3USCWUN-yCVsp7YC_Qevps1Xu_",
  playlists: [],
  playing: false,
  item: null
}

const reducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        user: action.user
      }
    case 'SET_TOKEN':
      return {
        ...state,
        token: action.token
      }
    case 'SET_PLAYLISTS':
      return {
        ...state,
        playlists: action.playlists
      }
    default:
      return state

  }

}

export default reducer;
