export const initialState = {
  user: null,
  // change token to null after development //TODO
  //token: null,
  token: "BQCAfSUFe-cQTy6ZhOJY18I4uxrapK9fLSIQ6bviicakXAR2CsbfcSYCICFbCK__ReFiTawbFlpEZ_blVtHcBRcdfvayEHQedeQaZoLz5vyMc888_yYxt9v2o5bOb1Zh3NCL0aNpMMrcNsBmdE2TdfcP1olvLDhsFA",
  playlists: [],
  spotify: null,
  playing: false,
  item: null,
  discover_weekly: null,
  top_artists: null,
  playing: false,
}

const reducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        user: action.user
      }

    case 'SET_TOKEN':
      return {
        ...state,
        token: action.token
      }

    case 'SET_PLAYLISTS':
      return {
        ...state,
        playlists: action.playlists
      }

    case 'SET_DISCOVER_WEEKLY':
      return {
        ...state,
        discover_weekly: action.discover_weekly,
      }

    case "SET_TOP_ARTISTS":
      return {
        ...state,
        top_artists: action.top_artists,
      }



    default:
      return state

  }

}

export default reducer;
